import unittest
import os.path
import tempfile
import shutil
import datetime

import phone_communication_backup_coalescer.sms as sms
from phone_communication_backup_coalescer.utils import ParseWarning


class SmsParseFileTestCase(unittest.TestCase):
    def setUp(self):
        self.backup_control = sms.SmsBackupControl()
        self.temp_dir = tempfile.mkdtemp('sms-parse-file-test')

    def tearDown(self):
        shutil.rmtree(self.temp_dir)

    def test_filename_pattern(self):
        self.assertIsNotNone(self.backup_control.filename_pattern)

